						["Nameplate Added"] = "function (self, unitId, unitFrame, envTable, modTable)\n    if UnitIsPlayer(unitId) then \n        if unitFrame.ExtraQuestMarker22154:IsShown() then\n            unitFrame.ExtraQuestMarker22154:Hide()\n        end\n        return \n    end\n    modTable.CheckForQuest (unitFrame)\nend\n\n\n",
