						["Constructor"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    if (not unitFrame.ExtraQuestMarker22154) then\n        unitFrame.ExtraQuestMarker22154 = unitFrame.healthBar:CreateTexture (nil, \"overlay\")\n        local texture = unitFrame.ExtraQuestMarker22154\n        if modTable.config.QIbagicon then\n            texture:SetAtlas(\"levelup-icon-bag\")\n        else\n            texture:SetTexture([[Interface\\OPTIONSFRAME\\UI-OptionsFrame-NewFeatureIcon]])\n        end\n        texture:SetSize (modTable.config.QIsize, modTable.config.QIsize)\n        Plater.SetAnchor (texture, modTable.Anchor)\n    end\nend\n\n\n\n\n",
